use Google\Cloud\Firestore\FieldValue;
use Google\Cloud\Firestore\FirestoreClient;

$firestore = new FirestoreClient;
$document = $firestore->document('users/dave');
$document->update([
    ['path' => 'hometown', 'value' => FieldValue::deleteField()]
]);

